import { useReducer } from "react";

const defaultValues = { value: "", isTouched: false };

const inputReducer = (state, action) => {
  if (action.type === "INPUT") {
    return { value: action.value, isTouched: state.isTouched };
  }
  if (action.type === "BLUR") {
    return { value: state.value, isTouched: true };
  }
  if (action.type === "RESET") {
    return { value: "", isTouched: false };
  }
  return defaultValues;
};

const useValidate = (validator) => {
  const [inputState, dispatchInput] = useReducer(inputReducer, defaultValues);

  const inputHandler = (event) => {
    dispatchInput({ type: "INPUT", value: event.target.value });
  };

  const touchHandler = () => {
    dispatchInput({ type: "TOUCH" });
  };

  const resetHandler = () => {
    dispatchInput({ type: "RESET" });
  };

  const hasError = !validator(inputState.value) && inputState.isTouched;
  console.log(hasError);
  return {
    inputValue: inputState.value,
    isTouched: inputState.isTouched,
    hasError,
    inputHandler,
    touchHandler,
    resetHandler,
  };
};

export default useValidate;
