import { useCallback, useEffect } from "react";
import useHttp from "../../hooks/use-http";

const AveilableMeals = () => {
  const { error, isLoading, sendRequest: getMeals } = useHttp();

  const logMeals = useCallback((meals) => {
    const melasList = meals.map((meal) => console.log(meal.id));
  }, []);

  useEffect(() => {
    getMeals(
      {
        url: "https://reactmeals-f6e16-default-rtdb.europe-west1.firebasedatabase.app/Meals.json",
      },
      logMeals
    );
  }, [getMeals, logMeals]);
};

export default AveilableMeals;
